    private void btnOkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnOkActionPerformed
        // TODO add your handling code here:
        String email = jtEmail.getText();
        String pass = jtPass.getText();
        ProcessEmpleado empObj = new ProcessEmpleado();
        if (email.isEmpty() || pass.isEmpty()){
            lbMensaje.setText("Complete todos los campos por favor.");
        }
        else{
            lbMensaje.setText("");
            if(empObj.validarCredenciales(cn, email, pass)){
                lbMensaje.setText("Felicidades!!!!");
                Principal jframePrincipal = new Principal();
                jframePrincipal.setVisible(true); 
                dispose();
            }
            else{
                lbMensaje.setText("Incorrecto");
            }
        }
    }//GEN-LAST:event_btnOkActionPerformed

